mapper.select:
SELECT a.id, a.name, a.open_id AS "openId", a.id_card AS "idCard", a.phone, a.art_type AS "artType", a.art_level AS "artLevel", a.status, a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.org_id AS "org.id", g.org_name AS "org.orgName" FROM sys_user_auth a ,sys_org g
a.del_flag=#{DEL_FLAG_NORMAL} and a.org_id=g.id
AND INSTR(a.name,#{name})<>0
AND a.create_date>=#{beginDate}
AND a.create_date<#{endDate}+1
AND a.art_type = #{artType}
AND a.org_id = #{org.id}
ORDER BY a.create_date DESC

mapper.insert:
INSERT INTO sys_user_auth_img(
	id,
	user_auth_id,
	img_url
) VALUES (
	#{id},
	#{userAuthId},
	#{imgUrl}
)

mapper.update:
UPDATE sys_user_auth SET
	status = #{status},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_user_auth SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
