mapper.select:
SELECT a.id, a.art_type AS "artType", a.model_type AS "modelType", a.title, a.user_id AS "user.id", b.nick_name AS "user.nickName", a.status, a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.org_id AS "org.id", g.org_name AS "org.orgName", (select count(t.id) from art_works_comment t where t.art_works_id=a.id AND t.status='1' and t.del_flag='0') AS "plNum" FROM art_works a ,sys_user b,sys_org g
a.del_flag=#{DEL_FLAG_NORMAL} and a.user_id = b.id and a.org_id=g.id
AND INSTR(b.nick_name,#{user.nickName})>0
AND INSTR(a.title,#{title})>0
AND a.update_date>=#{beginDate}
AND a.update_date<#{endDate}+1
AND a.art_type = #{artType}
AND a.user_id = #{user.id}
AND a.org_id = #{org.id}
ORDER BY a.update_date DESC

mapper.update:
UPDATE art_works SET
	status = #{status},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

mapper.select:
SELECT a.id, a.model_type, b.nick_name, b.photo, b.art_level, b.art_type, a.location, a.create_date, a.title, CASE WHEN a.model_type='0' THEN (SELECT t.content FROM art_works_content t where t.art_works_id=a.id and t.file_type='0' LIMIT 0,1) WHEN a.model_type='1' THEN (SELECT t.gif_url FROM art_works_content t where t.art_works_id=a.id and t.file_type='1' AND t.gif_url IS NOT NULL LIMIT 0,1) ELSE (SELECT t.content FROM art_works_content t where t.art_works_id=a.id and t.file_type='2' LIMIT 0,1) END content, a.dz_num, CASE WHEN a.status='1' THEN (SELECT count(*) FROM art_works_comment v WHERE v.art_works_id=a.id) ELSE 0 END pl_num, c.id c_id, d.nick_name c_nick_name, d.photo c_photo, d.art_level c_art_level, d.art_type c_art_type, c.content c_content, c.dz_num c_dz_num, c.create_date c_create_date, (SELECT count(*) FROM art_works_comment v WHERE v.parent=c.id) c_pl_num FROM art_works a INNER JOIN sys_user b ON a.user_id = b.id LEFT JOIN art_works_comment c ON a.id = c.art_works_id and c.status='1' and c.is_hot='1' LEFT JOIN sys_user d ON c.create_by=d.id
a.status ='1' AND a.del_flag='0'
AND a.art_type = #{artTypeParam}
AND a.create_date <=#{searchDate}
order by a.create_date DESC LIMIT #{offset},#{pageSize}
